import React from "react";
import { Route, Switch } from "react-router-dom";

import MainPage from "pages/MainPage/MainPage.js";
import NewArticle from "pages/NewArticle/NewArticle.js"
import Authentication from "pages/Authentication/Authentication.js"

export default (props) => {
  const { currentLang, setUserName, isLogin, setIsLogin } = props;
  return (
    <Switch>
      <Route path="/" component={() => <MainPage currentLang={currentLang} />} exact />
      <Route path="/login" component={() => <Authentication currentLang={currentLang} setIsLogin={setIsLogin} isLogin={isLogin} />} />
      <Route path="/register" component={() => <Authentication currentLang={currentLang} setIsLogin={setIsLogin} isLogin={isLogin} />} />
      <Route path="/new" component={() => <NewArticle />} currentLang={currentLang} />
      {/* <Route path="/articles/:slug" component={() => <Article currentLang={currentLang} />} /> */}
    </Switch>
  )
}
