import React, { useRef, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import LangsTranslate from "LangsTranslate/LangsTranslate.json";

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: "10px 0px",
      width: '100%',
    },
  },
}));

export default function CommentNew(props) {
  const classes = useStyles();
  const textFieldRef = useRef();
  const { currentLang } = props;

  useEffect(() => {
    setTimeout(() => {
      if (textFieldRef) {
        textFieldRef.current.focus();
      }
    }, 400)
  }, [])

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <TextField inputRef={textFieldRef} id="outlined-basic" label={LangsTranslate[currentLang].Feeds.feed["new-comment"]} variant="outlined" />
    </form>
  );
}
