import React from 'react';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import "./Authentication.scss";
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { withRouter } from "react-router-dom";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles({
  linkStyle: {
    fontSize: "16px",
    cursor: "pointer",
  }
})


const Authentication = (props) => {
  const classes = useStyles();
  const linkHandler = (e) => {
    e.preventDefault();
    props.history.push("/register");
  }
  return (
    <div className="Authentication">
      <div className="container">
        <div className="Authentication__body">
          <Paper>
            <div className="Authentication__auth auth">
              <Typography variant="h4" component="h2">
                Login
              </Typography>
              <form onSubmit={(e) => e.preventDefault()} action="#" method="GET">
                <fieldset className="auth__field">
                  <fieldset className="auth__email">
                    <TextField type="email" placeholder="your@gmail.com" label="Enter email" variant="filled" />
                  </fieldset>
                  <fieldset className="auth__pass">
                    <TextField type="password" placeholder="password" label="Enter password" variant="filled" />
                  </fieldset>
                  <Button variant="contained" color="primary" type="submit">Sign in</Button>
                </fieldset>
              </form>
              <p><Link onClick={linkHandler} className={classes.linkStyle} color="primary">Need an account?</Link></p>
            </div>
          </Paper>
        </div>
      </div>
    </div >
  );
}

export default withRouter(Authentication);