import React, { useState } from 'react';
import MUIEditor, { MUIEditorState } from 'react-mui-draft-wysiwyg';
import textEditorUk from 'pages/NewArticle/textEditorUk.js';

export default function TextEditor({ currentLang }) {
  console.log(currentLang);
  const configEN = {
    lang: 'en',
  }
  const configUK = {
    translations: textEditorUk
  }
  const [editorState, setEditorState] = useState(
    MUIEditorState.createEmpty(currentLang === 'uk' ? configUK : configEN),
  )
  const onChange = newState => {
    setEditorState(newState)
  }
  return (
    <MUIEditor
      editorState={editorState}
      onChange={onChange}
      config={currentLang === 'uk' ? configUK : configEN}
    />
  )
}

